import org.junit.jupiter.api.Test;

import static org.junit.jupiter.api.Assertions.*;

class AlternatingCharactersTest {

    @Test
    void alternatingCharacters() {

        int result = AlternatingCharacters.alternatingCharacters("AAAA");
        assertEquals(3, result);
        result = AlternatingCharacters.alternatingCharacters("");
        assertEquals(4930, result);
        result = AlternatingCharacters.alternatingCharacters("AABBABABBBBBABBABABBBBABBABABABBABBABBBBAAABBBBBBBBBBBABBBBBBBABBBBBBBBBBBBABBABBBBAABBBBBAAAABBBBBB");
        assertEquals(62, result);

    }
}